import Foundation

enum Categorys: String, Codable, Hashable {
    case futter = "Futter"
    case spielzeuge = "Spielzeuge"
    case klamotten = "Klamotten"
    case halsband = "Halsband"
    case hundereinigung = "Hundereinigung"
}

struct SubCategory: Codable, Hashable {
    let name: String
    let image: String
}

struct Category: Codable, Hashable {
    let category: Categorys
    let subCategories: [SubCategory]
    let image: String
}

